template<typename Real>
vec<Real, 3> blackbody(Real scalar) {
    scalar = std::clamp(scalar, Real(0), Real(1));
    // Keep ASAN happy:
    if (scalar == 1) {
        return vec<Real>(blackbody_r<Real>.back(),
                   blackbody_g<Real>.back(),
                   blackbody_b<Real>.back());
    }
    constexpr const Real h = Real(1)/(blackbody_r<Real>.size()-1);
    Real ii = std::floor(scalar/h);
    int i = static_cast<int64_t>(ii);
    Real t = scalar/h - ii;

    auto color = vec<Real, 3>((1-t)*blackbody_r<Real>[i] + t*blackbody_r<Real>[i+1],
                              (1-t)*blackbody_g<Real>[i] + t*blackbody_g<Real>[i+1],
                              (1-t)*blackbody_b<Real>[i] + t*blackbody_b<Real>[i+1]);
    return color;
}

